    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        Parttime p=new Parttime();
        p.setVisible(true);
        final_emp e=new final_emp();
        e.setid(Integer.parseInt(txtEmpid.getText().trim()));
        e.setName((txtEmpid.getText().trim()));
        
       
            e.setdob((txtEmpdob.getText().trim()));
         
        e.setmarryid(Integer.parseInt(txtmarridid.getText().trim()));
         
        
            e.set_marrydate((txtmarrieddate.getText().trim()));
            
            
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton5ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton5ActionPerformed
        //DELETE BUTTON
        if("".equals(txtEmpid.getText().trim()))
        {
            lblmessage.setText("EmpId cannot Be Empty");
            
        }
        else{
            Emp_manager em=new Emp_manager ();
          boolean status=  em.deleteByid(Integer.parseInt(txtEmpid.getText().trim()));
          if(status=true)
          {
              lblmessage.setText("Delete succsesfull");
          }
          else
          {
              lblmessage.setText("Delete unsuccsesfull");
          }
            
        }
    }//GEN-LAST:event_jButton5ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        //INSERT BUTTON
        final_emp e=new final_emp();
        
        if("".equals(txtEmpid.getText().trim()))
        {
            lblmessage.setText("EmpId cannot Be Empty");
            
        }
         else{
             
             e.setid(Integer.parseInt(txtEmpid.getText().trim()));
             e.setName(txtEmpname.getText().trim());
            
                e.setdob((txtEmpdob.getText()));
            
            e.setmarryid(Integer.parseInt(txtmarridid.getText().trim()));
            
                e.set_marrydate((txtmarrieddate.getText()));
           
            Emp_manager em=new Emp_manager();
            boolean status=em.insert(e);
            if(status=true)
            {
                lblmessage.setText("Insert Succsesfull");
            }
            else
            {
                lblmessage.setText("Insert Again");
            }
            
         }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        //UPDATE BUTTON 
        if("".equals(txtEmpid.getText().trim()))
             
        {
            lblmessage.setText("EmpId cannot Be Empty");
            
        }
        else
         {
             final_emp e=new final_emp();
             e.setid(Integer.parseInt(txtEmpid.getText()));
             e.setName(txtEmpname.getText());
             Emp_manager em=new Emp_manager();
            boolean status= em.update(e);
             if(status=true)
            {
                lblmessage.setText("Update Succsesfull");
            }
            else
            {
                lblmessage.setText("Update Again");
            }
             
         }
    }//GEN-LAST:event_jButton4ActionPerformed

