    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
            final_emp e=new final_emp();
            if("".equals(txtEmpid.getText().trim()))
             
        {
            lblmessage.setText("EmpId cannot Be Empty");
            
        }
        else
            {
                Emp_manager em=new Emp_manager();
                e=em.searchById(Integer.parseInt(txtEmpid.getText()));
               if(e.getName()==null)
               {
                   lblmessage.setText("No records");
               }
               else
               {
                   txtEmpid.setText(String.valueOf(e.getid()));
                txtEmpname.setText(e.getName());
                txtEmpdob.setText(e.getdate());
                txtmarridid.setText(String.valueOf(e.getmarryid()));
                txtmarrieddate.setText(e.getmarrydate());
                
               }
                
            }
            
    }//GEN-LAST:event_jButton6ActionPerformed

